import react, {useState} from "react";
import ExpenseForm from "./ExpenseForm";
import './NewExpense.css';

const NewExpense = (props) => {
    const onSaveExpenseDataHandler = (enteredExpenseData) => {
        const expenseData = {
            ...enteredExpenseData,
            id: Math.random().toString()
        };
       props.onAddExpense(expenseData);
    };
    const [displayNewExpense, setdisplayNewExpense] = useState(false);

    const displayNewExpenseHandler = (prevValue) => {
        setdisplayNewExpense(!prevValue);
    }

    // let displayNewExpense = false;
    let expenseDisplay = <ExpenseForm onSaveExpenseData={onSaveExpenseDataHandler}/>
    
    if (!displayNewExpense)
    {
      expenseDisplay = <div className="new-expense__actons">
          <button type="submit" onClick={displayNewExpenseHandler} >Add New Expense</button>
        </div>
     
    }

return(
    <div className="new-expense">
        {expenseDisplay}
    </div>
)
}

export default NewExpense;
