import Card from "../UI/Card";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./../ExpenseFilter/ExpenseFilter";
import "./Expenses.css";
import { useState } from "react";

function Expenses(props) {
  const [filterDate, setfilterDate] = useState("2020");
  const [listToDisplay, setlistToDisplay] = useState(props.items);

  const onSetFilterHandler = (filterValue) => {
    setfilterDate(filterValue);
    // listToDisplay = props.items.filter((x) => x.date.getFullYear() == filterDate);
    setlistToDisplay(props.items.filter((x) => x.date.getFullYear() == filterDate));
  };
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selectedYear={filterDate}
          onSetFilter={onSetFilterHandler}
        />
        {listToDisplay.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
        {/* <ExpenseItem
          title={props.items[0].title}
          amount={props.items[0].amount}
          date={props.items[0].date}
        />
        <ExpenseItem
          title={props.items[1].title}
          amount={props.items[1].amount}
          date={props.items[1].date}
        />
        <ExpenseItem
          title={props.items[2].title}
          amount={props.items[2].amount}
          date={props.items[2].date}
        />
        <ExpenseItem
          title={props.items[3].title}
          amount={props.items[3].amount}
          date={props.items[3].date}
        /> */}
      </Card>
    </div>
  );
}

export default Expenses;
